tool.command:
ln -s "${input_vcf}" input.vcf.gz &&
bcftools index input.vcf.gz &&
bcftools call 
    -o "${output}"
    -O ${output_format}
    #if str($filter_conditional.filter_calls_selector) == 'regions':
        -R "${filter_conditional.input_regions}"
    #else if str($filter_conditional.filter_calls_selector) == 'samples':
        -S "${filter_conditional.input_regions}"
        ${filter_conditional.output_ploidy}
    #else if str($filter_conditional.filter_calls_selector) == 'targets':
        -T "${filter_conditional.input_regions}"
    #end if
    ${variants_only}
    ${skip_indels_snps}
    ${skip_snps}
    ${caller}
    input.vcf.gz
